#include "dh.h"

void main(int c, char **v)
{
	u m[DH_SIZE], g[DH_SIZE], e[DH_SIZE], b[DH_SIZE];
	char e_str[DH_SIZE] = "FFFFFC90FDAA22168C234C4C6628B80D";
	//2048-bit MODP Group (https://www.ietf.org/rfc/rfc3526.txt)
	char g_str[DH_SIZE] = "2";
	char m_str[DH_SIZE] = "";
	int S = (DH_SIZE / 8) + 1;
	
	//convert hex_str to digit
	tohex(g_str, g, S);
	tohex(e_str, e, S);
	tohex(m_str, m, S);

	//compute g ^ e % m
	exp_mod(g,e,m,b,S);

	getchar();

}
